import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators, FormGroupDirective, NgForm } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { AppComponent } from '../app.component';
import { UserDataService } from '../user-data.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {

  constructor (
    private http: HttpClient,
    private app: AppComponent,
        private userData: UserDataService,
        private router: Router
  ){

  }

registerForm = new FormGroup({
  name: new FormControl('', [Validators.required]),
  email: new FormControl('', [Validators.required, Validators.email]),
  password: new FormControl('', [Validators.required]),

})

onSubmit(){

  const name = this.registerForm.controls['name'].value;
  const email = this.registerForm.controls['email'].value;
  const password = this.registerForm.controls['password'].value;

  let userdata = {
    name: name,
    email: email,
    password: password
  }
  return this.http.post('http://127.0.0.1:8000/api/register', userdata).subscribe((response) => {
    this.userData.setuserdata(response);
    this.router.navigate(['/main'])
  })

}

}
<div class="container">
  <h2>Registro de Usuario</h2>
  <form [formGroup]="registerForm" (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="name">Nombre:</label>
      <input type="text" formControlName="name" class="form-control" id="name" placeholder="Ingrese su nombre">
    </div>
    <div class="form-group">
      <label for="email">Correo electrónico:</label>
      <input type="email" formControlName="email" class="form-control" id="email" placeholder="Ingrese su correo electrónico">
    </div>
    <div class="form-group">
      <label for="password">Contraseña:</label>
      <input type="password" formControlName="password" class="form-control" id="password" placeholder="Ingrese su contraseña">
    </div>
    <button type="submit" class="btn btn-primary">Registrarse</button>
  </form>
</div>
